import React from 'react';
import { Calendar, ArrowRight } from 'lucide-react';

function Blog() {
  const posts = [
    { 
      id: 1, 
      title: 'Getting Started with React Hooks', 
      excerpt: 'Learn how to use React Hooks to manage state and side effects in your functional components...',
      date: '2023-06-01',
      image: '/placeholder.svg?height=200&width=300'
    },
    { 
      id: 2, 
      title: 'Building Responsive Layouts with CSS Grid', 
      excerpt: 'Discover how to create complex, responsive layouts easily using CSS Grid...',
      date: '2023-06-15',
      image: '/placeholder.svg?height=200&width=300'
    },
    { 
      id: 3, 
      title: 'Introduction to Server-Side Rendering with Next.js', 
      excerpt: 'Explore the benefits of server-side rendering and how to implement it using Next.js...',
      date: '2023-06-30',
      image: '/placeholder.svg?height=200&width=300'
    },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-100 to-indigo-100 py-16">
      <div className="container mx-auto px-4">
        <h1 className="text-4xl font-bold text-purple-800 mb-8 text-center">Blog</h1>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {posts.map(post => (
            <div key={post.id} className="bg-white rounded-lg shadow-md overflow-hidden">
              <img src={post.image} alt={post.title} className="w-full h-48 object-cover" />
              <div className="p-6">
                <h2 className="text-2xl font-bold text-purple-600 mb-2">{post.title}</h2>
                <p className="text-gray-700 mb-4">{post.excerpt}</p>
                <div className="flex justify-between items-center">
                  <p className="text-sm text-gray-500 flex items-center">
                    <Calendar size={16} className="mr-1" /> {post.date}
                  </p>
                  <a href={`/blog/${post.id}`} className="text-purple-600 hover:text-purple-800 flex items-center">
                    Read More <ArrowRight size={16} className="ml-1" />
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Blog;
